import { takeLatest, put, all, call } from 'redux-saga/effects';
import {userActionTypes} from'../user/user.types'
import {createUserProfileDocument, googleProvider, auth, getCurrentUser} from '../../firebase/firebase.utils';
import {SignInSuccess,SignInFailure, signOutFailure,signOutSuccess } from './user.actions'


export function* getSnapshotFromUserAuth(userAuth) {
    try {
    const userRef = yield call(createUserProfileDocument, userAuth)
    const userSnapShot = yield userRef.get();
    yield put(SignInSuccess({id: userSnapShot.id, ...userSnapShot.data()}))
} catch(error) {
    yield put(SignInFailure(error));
}
}


export function* signInWithGoogle() {
    try {
        const { user } = yield auth.signInWithPopup(googleProvider);
        yield getSnapshotFromUserAuth(user)
    } catch(error) {
        yield put(SignInFailure(error));
    }
}


export function* signInWithEmail({payload: {email, password}}) {
    try{
        const { user } = yield auth.signInWithEmailAndPassword(email, password)
        yield getSnapshotFromUserAuth(user)
    } catch(error) {
        put(SignInFailure(error))
    }
}

export function* isUserAuthenticated() {
    try {
        const userAuth = yield getCurrentUser();
        if (!userAuth) return;
        yield getSnapshotFromUserAuth(userAuth)
    } catch(error) {
        yield put(SignInFailure(error))
    }
}

export function* signOut() {
    try{
        yield auth.signOut()
        yield (put(signOutSuccess()))
    } catch(error) {
        yield (put(signOutFailure(error)))
    }
}

export function* onGoogleSignInStart() {
    yield takeLatest(userActionTypes.GOOGLE_SIGN_IN_START, signInWithGoogle)
}


export function* onEmailSignInStart() {

    yield takeLatest(userActionTypes.EMAIL_SIGN_IN_START, signInWithEmail)

}

export function* onCheckUserSession() {
    yield takeLatest(userActionTypes.CHECK_USER_SESSION, isUserAuthenticated )
}


export function* onSignOutStart() {
    yield takeLatest(userActionTypes.SIGN_OUT_START,)
}


export function* userSagas() {
    yield all([call(onGoogleSignInStart), call(onEmailSignInStart), call(onCheckUserSession)])
}
